INSERT INTO banner
(banner_no
, user_no
, podcast_no
, studio_no
, performance_no
, cost_no
, pay_no
, banner_product
, banner_company
, banner_filename
, banner_count
, banner_date
, banner_servicestart
, banner_serviceend
, banner_status)
SELECT LPAD(MAX(RIGHT(banner_no,3)) +1,10,'banner_00')
, #{userNo}
, #{podcastNo}
, #{studioNo}
, #{performanceNo}
, #{costNo}
, #{payNo}
, #{bannerProduct}
, #{bannerCompany}
, #{bannerFileName}
, #{bannerCount}
, SYSDATE() 
, #{bannerServiceStart}
, #{bannerServiceEnd}
, #{bannerStatus}
FROM banner
