mapper.select:
select customercenter.customercenter_no AS customercenterNo, customercenter.user_no AS userNo, user.user_nickname AS userNickname, customercenter.category_no AS categoryNo, category.category_sub AS categorySub, customercenter.customercenter_title AS customercenterTitle, customercenter.customercenter_content AS customercenterContent, DATE_FORMAT(customercenter_date,'%p %Y-%m-%d %h:%i') AS customercenterDate from customercenter
inner join user inner join category on customercenter.user_no = user.user_no and customercenter.category_no = category.category_no
ORDER BY customercenter_date DESC LIMIT #{pageHelper.startPage}, #{pageHelper.linePerPage}
