mapper.select:
select notice.notice_no AS noticeNo, notice.user_no AS userNo, user.user_nickname AS userNickname, notice.notice_title AS noticeTitle, notice.notice_content AS noticeContent, DATE_FORMAT(notice_date,'%p %Y-%m-%d %h:%i') AS noticeDate from notice inner join user on notice.user_no = user.user_no
notice_no LIKE CONCAT ('%',#{word},'%') OR notice_title LIKE CONCAT ('%',#{word},'%')
ORDER BY notice_no DESC LIMIT #{pageHelper.startPage}, #{pageHelper.linePerPage}
