mapper.insert:
INSERT INTO podmeeting
	(podmeeting_no
	, podcast_no
	, episode_no
	, user_no
	, category_no
	, podmeeting_title
	, podmeeting_content
	, podmeeting_pw
	, podmeeting_date)
VALUES
	(#{podMeetingNo}
	, #{podcastNo}
	, #{episodeNo}
	, #{userNo}
	, #{categoryNo}
	, #{podMeetingTitle}
	, #{podMeetingContent}
	, #{podMeetingPw}
	, SYSDATE())

mapper.select:
SELECT p.podmeeting_no AS podMeetingNo , p.user_no AS userNo , p.podmeeting_title AS podMeetingTitle , p.podmeeting_content AS podMeetingContent , DATE_FORMAT(p.podmeeting_date,'%p %Y-%m-%d %h:%i') AS podMeetingDate , u.user_nickname AS userNickname FROM podmeeting AS p INNER JOIN user AS u ON p.user_no = u.user_no
podmeeting_no LIKE CONCAT ('%',#{word},'%') OR podmeeting_title LIKE CONCAT ('%',#{word},'%')
ORDER BY podmeeting_no DESC LIMIT #{pageHelper.startPage}, #{pageHelper.linePerPage}
