mapper.select:
select studio.studio_no AS studioNo, studio.user_no AS userNo, user.user_nickname AS userNickName, studio.studio_name AS studioName, studio.studio_content AS studioContent, studio.studio_image AS studioImage from studio inner join user on studio.user_no = user.user_no
studio.studio_name LIKE CONCAT ('%',#{word},'%') OR studio.studio_content LIKE CONCAT ('%',#{word},'%') OR studio.studio_no LIKE CONCAT ('%',#{word},'%') OR user.user_nickname LIKE CONCAT ('%',#{word},'%')
ORDER BY studio_no DESC LIMIT #{pageHelper.startPage}, #{pageHelper.linePerPage}

mapper.insert:
INSERT INTO studio
	(studio_no
	, user_no
	, pay_no
	, category_no
	, studioreservation_no
	, banner_no
	, studio_name
	, studio_content
	, studio_image)
	SELECT (
	CASE
	WHEN COUNT(1) = 0
	THEN 'studio_001'
	ELSE LPAD(MAX(RIGHT(studio_no,3)) +1, 10, 'studio_00')
	END
	)
	,#{user_no}
	,#{pay_no}
	,#{category_no}
	,#{studioreservation_no}
	,#{banner_no}
	,#{studio_name}
	,#{studio_content}
	,#{studio_image}
	form studio

mapper.update:
UPDATE studio
SET
studio_name=${studioName},
studio_content=${studioContent},
studio_image=${studioImage}
WHERE studio_no=${studioNo}
