mapper.insert:
INSERT INTO episode
	(episode_no
	, user_no
	, podcast_no
	, good_no
	, listen_no
	, episode_title
	, episode_content
	, episode_audio
	, episode_video
	, episode_date))
	SELECT LPAD(MAX(RIGHT(episode_no,3)) +1,11,'episode_00')
		, #{userNo}
		, #{podcastNo}
		, #{goodNo}
		, #{listenNo}
		, #{episodeTitle}
		, #{episodeContent}
		, #{episodeAudio}
		, #{episodeVideo}
		, SYSDATE() 
	FROM episode
